import {Box,Button,Checkbox,Container,FormControlLabel,Grid,Link,Typography,TextField} from '@mui/material';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import React, { useState } from 'react';
// const cors = require('cors');
// app.use(cors());
function Login() {
    const [email, setEmail] = useState('');
    const [isValid, setIsValid] = useState(true);

    const handleChange = (e) => {
        setEmail(e.target.value);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        // Validate email format
        const isValidEmail = validateEmail(email);
        setIsValid(isValidEmail);
        let formData = {
            action:'login_user',
            'email':email
        }
        if (isValidEmail) {
          // Handle form submission
          try {
            const response = await fetch('http://192.168.97.167/AjaxController.php', {
              method: 'POST',
               headers: {
                'Content-Type': 'application/json',
                // Optionally, add any other headers required by the target server
            },
              body: JSON.stringify(formData)
            });
            const responseData = await response.json();
            console.log('Response:', responseData);
            // Handle response data as needed
          } catch (error) {
            console.error('Error submitting data:', error);
          }
        }
    };

    const validateEmail = (email) => {
        // Regular expression for email validation
        const regex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return regex.test(email);
    };

    // const fetchData = async () => {
    //     try {
    //       const response = await fetch('http://192.168.97.167/AjaxController.php');
    //       if (!response.status == 'OK') {
    //         throw new Error('Network response was not ok');
    //       }
    //       const data = await response.json();
    //     //   setResponseData(data);
    //     } catch (error) {
    //       console.error('Error fetching data:', error);
    //     }
    // };

    return (
        <>
            <Container component="main" maxWidth="xs" sx={{my:20}} >
                <Card>
                    <CardContent>
                        <Box>
                            <Typography component="h1" variant="h5">Sign In </Typography>
                            <Box component="form">
                                <TextField
                                    margin="normal"
                                    required
                                    fullWidth
                                    id="userEmail"
                                    type="email"
                                    label="Email Address"
                                    name="email"
                                    onChange={handleChange}
                                    error={!isValid}
                                    helperText={!isValid && 'Please enter a valid email address'}
                                    value={email} 
                                    autoFocus
                                />

                                <TextField
                                    margin="normal"
                                    required
                                    fullWidth
                                    name="password"
                                    label="Password"
                                    type="password"
                                    id="password"
                                />
                                {/* <FormControlLabel
                                control={<Checkbox value="remember" color="primary" />}
                                label="Remember me"
                                /> */}
                                <Button onClick={handleSubmit}
                                    type="submit"
                                    fullWidth
                                    variant="contained"
                                    sx={{ mt: 3, mb: 2 }}
                                    >Sign In
                                </Button>
                            <Grid>
                            <Link href="">Forgot password?</Link>
                            </Grid>    
                            <Grid className="footer">
                            <Typography component="h5">
                            Don't have an account? <Link href="">Sign Up</Link>
                            </Typography>
                            </Grid>
                            </Box>
                        </Box>
                    </CardContent>
                </Card>
            </Container>
        </>
    );
}

   export default Login;
